import { Component } from "react";

export default class MainContent extends Component{
    state ={
        pageTitile:"Customer",
        customerCount:5,
        customers:[
            {
                id:1 ,name:"scott",phone:" 123-456"
            },
            {
                id:2 ,name:"jones",phone:" 123-456"
            },
            {
                id:3 ,name:"john",phone:" 123-456"
            },
            {
                id:4 ,name:"allen",phone:" 123-456"
            }
        ]
    };

    render()
    {
        return<div>
            <h1 className="border-bottom m-1 p-1">{this.state.pageTitile}
            <span className="badge badge-secondary m-2">{this.state.customerCount}</span>
           <button className="btn btn-info" onClick={this.onRefreshClick} >Refresh</button>
            </h1>
            <table className="table"></table>
            <th>id</th>
            <th>name</th>
            <th>n</th>
        </div>
    }
    // adding event when refresh button is clicked
    onRefreshClick =() =>
    {
        this.setState({
            customerCount:7
        })
        console.log("refresh button clicked")
    }
}
